function mask(e) { if (10 == e.value.length) { if (" " != e.value[4]) { var u = "(" + e.value.substring(0, 2) + ") ", l = e.value.substring(2, 6) + "-", a = e.value.substring(6, 10); e.value = u + l + a } } else if (13 == e.value.length) { if ("-" != e.value[9]) { u = e.value.substring(0, 9), a = e.value.substring(9, 13); e.value = u + "-" + a } } else if (14 == e.value.length) { if ("-" != e.value[9]) { u = e.value.substring(0, 9), a = e.value.substring(11, 14); e.value = u + "-" + e.value[9] + a } } else if (15 == e.value.length && "-" != e.value[10]) { u = e.value.substring(0, 9), a = e.value.substring(11, 15); e.value = u + e.value[10] + "-" + a } }